    private void cadastroClientesButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cadastroClientesButtonActionPerformed
         if(pfOption.isSelected()){
             Cliente novoClientePF = new ClientePF(nomeInput.getText(),enderecoInput.getText(),idInput.getText());
             JOptionPane.showMessageDialog(null, nomeInput.getText()+enderecoInput.getText()+idInput.getText());
             boolean cadastrou = sistema.cadastrarCliente(novoClientePF);
             
             if(cadastrou)JOptionPane.showMessageDialog(null, "ClientePF cadastrado com sucesso!");
             else JOptionPane.showMessageDialog(null, "Erro ao cadastrar");
         }
         else if(pjOption.isSelected()){
             Cliente novoClientePJ = new ClientePJ(nomeInput.getText(),enderecoInput.getText(),idInput.getText());
             
             boolean cadastrou = sistema.cadastrarCliente(novoClientePJ);
             
             if(cadastrou)JOptionPane.showMessageDialog(null, "Cliente PJ cadastrado com sucesso!");
             else JOptionPane.showMessageDialog(null, "Erro ao cadastrar");
         }
         else{
             JOptionPane.showMessageDialog(null, "Dados faltando..");
         }
    }//GEN-LAST:event_cadastroClientesButtonActionPerformed

